sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    try {
        var postRequest = new sn_ws.RESTMessageV2('Product Catalog Salesforce', 'Post Product Catalog Changes');
        var patchRequest = new sn_ws.RESTMessageV2('Product Catalog Salesforce', 'Update Product Catalog Changes');
        var name = current.name;
        var description = current.description;
        var family = current.u_family;
        var status = current.status;
        var externalId = current.external_id;
        var active = "true";
        var req;
        var response;
        var responseBody;
        var httpStatus;

        if (status != 'published') {
            active = "false";
        }

        if (externalId == "") {
            req = '{"Name": "' + name + '", "Description": "' + description + '", "Family": "' + family + '", "isActive": "' + active + '", "Entity__c": "Flag"}';
            gs.info(req);
            postRequest.setRequestHeader("Content-Type", "application/json");
            postRequest.setRequestBody(req);
            response = postRequest.execute();
			var responseObject = JSON.parse(response.getBody());
            gs.info(responseBody);
            var productOff = new GlideRecord('sn_prd_pm_product_offering');
            productOff.addQuery('sys_id', current.sys_id);
            productOff.query();
            if (productOff.next()) {
                productOff.external_id = responseObject.id;
                productOff.update();
            }

            httpStatus = response.getStatusCode();
        } else {
            req = '{"Name": "' + name + '", "Description": "' + description + '", "Family": "' + family + '", "isActive": "' + active + '", "Entity__c": "Flag"}';
            gs.info(req);
            patchRequest.setRequestHeader("Content-Type", "application/json");
			var url = "https://gcx--prodaptdev.sandbox.my.salesforce.com/services/data/v56.0/sobjects/Product2/" + externalId;
            patchRequest.setEndpoint(url);
            patchRequest.setRequestBody(req);
            response = patchRequest.execute();
            responseBody = response.getBody();
            gs.info(responseBody);
            httpStatus = response.getStatusCode();

        }


    } catch (ex) {
        var message = ex.message;
        gs.error(message);
    }

})(current, previous);
