sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var req = request.body.data;
    var status;
    var statusCode;
    var result = {};
    var gcxValidationUtil = new GCXValidationUtil;

    var account = req.account;
    var contact = req.contact;
    var location = req.location;

    if (!account || !contact) {
        response.setStatus(422);
        result.body = 'Missing Account/Contact';
        response.setBody(result);
        return;
    }
    try {
        var accountValidationList = gcxValidationUtil.validateAccount(account);
        var contactValidationList = gcxValidationUtil.validateContact(contact);
        // if (location.length > 0) {
        //     var locationValidationList = gcxValidationUtil.validateLocation(location);
        // }

        if ((accountValidationList.length > 0) || (contactValidationList.length > 0)) {
            result.account = accountValidationList;
            result.contact = contactValidationList;
          //result.location = locationValidationList;
            statusCode = 422;
        } else {
			var createCustomerSupplierAccount = new CreateCustomerSupplierAccount;
			account.gcxEntity = "FLAG";
			result = createCustomerSupplierAccount.createAccount(account,contact,location);
            statusCode = result.status;
			delete result.status;
    }
} catch (error) {
        response.setStatus(500);
        result.body = error.message;
        response.setBody(result);
        return;
    }

    response.setBody(result);
    response.setStatus(statusCode);

})(request, response);
